"CREATE TABLE IF NOT EXISTS user_settings (id UUID DEFAULT uuid_generate_v4() PRIMARY KEY, user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE, targetLanguage TEXT NOT NULL DEFAULT 'english', creationTargetLanguage TEXT NOT NULL DEFAULT 'english', studyTargetLanguage TEXT NOT NULL DEFAULT 'english', studyMode TEXT NOT NULL DEFAULT 'audio_image_text', created_at TIMESTAMP WITH TIME ZONE DEFAULT TIMEZONE('utc'::text, NOW()) NOT NULL);" 
